import React, { useEffect, useState } from 'react';
import API from '../api';

function Profile() {
  const [profile, setProfile] = useState(null);
  const [prefs, setPrefs] = useState({ cuisine: [], priceRange: '', location: '' });

  useEffect(() => {
    API.get('/auth/profile').then(res => setProfile(res.data));
    API.get('/users/preferences').then(res => setPrefs(res.data));
  }, []);

  const handleChange = e => {
    setPrefs({ ...prefs, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    await API.put('/users/preferences', prefs);
    alert('Preferences updated!');
  };

  if (!profile) return <div>Loading...</div>;

  return (
    <div>
      <h2>Profile</h2>
      <div>Name: {profile.name}</div>
      <div>Email: {profile.email}</div>
      <h3>Preferences</h3>
      <input name="cuisine" value={prefs.cuisine} onChange={handleChange} placeholder="Cuisine (comma separated)" />
      <input name="priceRange" value={prefs.priceRange} onChange={handleChange} placeholder="Price Range" />
      <input name="location" value={prefs.location} onChange={handleChange} placeholder="Location" />
      <button onClick={handleSave}>Save Preferences</button>
    </div>
  );
}

export default Profile;
