import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import API from '../api';
import { Card, CardContent, Typography, Button, Grid, Dialog, DialogTitle, DialogContent, DialogActions, TextField, Rating, Box } from '@mui/material';

function Restaurant() {
  const { id } = useParams();
  const [restaurant, setRestaurant] = useState(null);
  const [open, setOpen] = useState(false);
  const [review, setReview] = useState({ rating: 5, comment: '' });

  useEffect(() => {
    API.get(`/restaurants/${id}`).then(res => setRestaurant(res.data));
  }, [id]);

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const handleReviewChange = e => setReview({ ...review, [e.target.name]: e.target.value });
  const handleRatingChange = (_, value) => setReview({ ...review, rating: value });

  const handleSubmit = async () => {
    await API.post(`/restaurants/${id}/reviews`, review);
    setOpen(false);
    API.get(`/restaurants/${id}`).then(res => setRestaurant(res.data));
  };

  if (!restaurant) return <div>Loading...</div>;

  return (
    <Box>
      <Card sx={{ mb: 3 }}>
        <CardContent>
          <Typography variant="h4">{restaurant.name}</Typography>
          <Typography variant="body1" sx={{ mb: 1 }}>{restaurant.description}</Typography>
          <Typography variant="body2" color="text.secondary">Cuisine: {restaurant.cuisine?.join(', ')}</Typography>
          <Typography variant="body2" color="text.secondary">Location: {restaurant.location}</Typography>
          <Button component={Link} to={`/menus/${restaurant._id}`} variant="contained" sx={{ mt: 2 }}>View Menu</Button>
          <Button onClick={handleOpen} variant="outlined" sx={{ mt: 2, ml: 2 }}>Add Review</Button>
        </CardContent>
      </Card>
      <Typography variant="h5" sx={{ mb: 2 }}>Reviews</Typography>
      <Grid container spacing={2}>
        {restaurant.reviews?.map(r => (
          <Grid item xs={12} sm={6} md={4} key={r._id}>
            <Card>
              <CardContent>
                <Rating value={r.rating} readOnly max={5} />
                <Typography variant="body2">{r.comment}</Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Add a Review</DialogTitle>
        <DialogContent>
          <Rating name="rating" value={review.rating} onChange={handleRatingChange} max={5} />
          <TextField
            autoFocus
            margin="dense"
            name="comment"
            label="Comment"
            type="text"
            fullWidth
            variant="standard"
            value={review.comment}
            onChange={handleReviewChange}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleSubmit} variant="contained">Submit</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

export default Restaurant;
