import React, { useEffect, useState } from 'react';
import API from '../api';

function Reservations() {
  const [reservations, setReservations] = useState([]);

  useEffect(() => {
    API.get('/reservations/user/me').then(res => setReservations(res.data));
  }, []);

  const handleCancel = async (id) => {
    await API.delete(`/reservations/${id}`);
    setReservations(reservations => reservations.filter(r => r._id !== id));
  };

  return (
    <div>
      <h2>Your Reservations</h2>
      <ul>
        {reservations.map(r => (
          <li key={r._id}>
            {r.restaurant} on {new Date(r.date).toLocaleDateString()} at {r.time} for {r.partySize} people
            {r.status !== 'cancelled' && <button onClick={() => handleCancel(r._id)}>Cancel</button>}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Reservations;
