import React, { useEffect, useState } from 'react';
import API from '../api';
import { Link } from 'react-router-dom';
import { Grid, Card, CardContent, CardMedia, Typography, Button, TextField, Box } from '@mui/material';

function Home() {
  const [restaurants, setRestaurants] = useState([]);
  const [query, setQuery] = useState('');

  useEffect(() => {
    API.get('/restaurants').then(res => setRestaurants(res.data));
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    const res = await API.get('/restaurants', { params: { q: query } });
    setRestaurants(res.data);
  };

  return (
    <Box>
      <Typography variant="h3" align="center" gutterBottom>Find Your Next Meal</Typography>
      <Box component="form" onSubmit={handleSearch} sx={{ display: 'flex', justifyContent: 'center', mb: 4 }}>
        <TextField
          value={query}
          onChange={e => setQuery(e.target.value)}
          placeholder="Search restaurants..."
          variant="outlined"
          sx={{ mr: 2, width: 300 }}
        />
        <Button type="submit" variant="contained">Search</Button>
      </Box>
      <Grid container spacing={3}>
        {restaurants.map(r => (
          <Grid item xs={12} sm={6} md={4} key={r._id}>
            <Card>
              {r.image && <CardMedia component="img" height="140" image={r.image} alt={r.name} />}
              <CardContent>
                <Typography variant="h6" component={Link} to={`/restaurants/${r._id}`} sx={{ textDecoration: 'none', color: 'inherit' }}>{r.name}</Typography>
                <Typography variant="body2" color="text.secondary">{r.cuisine?.join(', ')}</Typography>
                <Typography variant="body2" color="text.secondary">{r.location}</Typography>
                <Button component={Link} to={`/restaurants/${r._id}`} variant="outlined" sx={{ mt: 1 }}>View Details</Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}

export default Home;
