import React, { useEffect, useState } from 'react';
import API from '../api';
import { Link } from 'react-router-dom';

function Home() {
  const [restaurants, setRestaurants] = useState([]);
  const [query, setQuery] = useState('');

  useEffect(() => {
    API.get('/restaurants').then(res => setRestaurants(res.data));
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    const res = await API.get('/restaurants', { params: { q: query } });
    setRestaurants(res.data);
  };

  return (
    <div>
      <h1>Restaurant Bot</h1>
      <form onSubmit={handleSearch}>
        <input value={query} onChange={e => setQuery(e.target.value)} placeholder="Search restaurants..." />
        <button type="submit">Search</button>
      </form>
      <ul>
        {restaurants.map(r => (
          <li key={r._id}>
            <Link to={`/restaurants/${r._id}`}>{r.name}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Home;
